msg.payload = "";
if (typeof msg.tweet.entities.media !== 'undefined') {
  if (typeof msg.tweet.entities.media[0].media_url !== 'undefined') {
    msg.payload = msg.tweet.entities.media[0].media_url;
  }  
}

if (typeof msg.tweet.extended_tweet !== 'undefined') {
    if (typeof msg.tweet.extended_tweet.entities !== 'undefined') {
        if (typeof msg.tweet.extended_tweet.entities.media !== 'undefined') {
            if (typeof msg.tweet.extended_tweet.entities.media[0].media_url !== 'undefined') {
                msg.payload = msg.tweet.extended_tweet.entities.media[0].media_url_https;
            }
        }
    }
}

if(typeof msg.tweet.retweeted_status !== 'undefined') {
    if( typeof msg.tweet.retweeted_status.extended_tweet !== 'undefined') {
        if( typeof msg.tweet.retweeted_status.extended_tweet.entities !== 'undefined') {
            if( typeof msg.tweet.retweeted_status.extended_tweet.entities.media !== 'undefined') {
                if( typeof msg.tweet.retweeted_status.extended_tweet.entities.media[0].media_url !== 'undefined') {
                    msg.payload = msg.tweet.retweeted_status.extended_tweet.entities.media[0].media_url;
                }
            }
        }
    }
}


if( msg.payload.length ) {
    msg.template = "<img width=\"300\" height=\"300\" alt=\"Twitter Image\" src=\""+msg.payload+"\">";
    return [ msg, null ];
} else {
    return [ null, msg ];
}

if (typeof msg.result == 'undefined') {
    return null;
}

if (typeof msg.result.error != 'undefined') {
    //The Lite Plan allows users to make 7,500 API calls for free
    // Daily limit is  (up to 250 calls per day) 
    // {"status":"ERROR","statusInfo":"Key is over transaction limit"}
    msg.template = msg.result.error.message;
    return msg;
}

// Text Extraction
if (typeof msg.result.images[0].text != 'undefined') {
    var image_text = msg.result.images[0].text;
    msg.payload = image_text;
    msg.template = image_text;
    if( image_text.length >0 ) {
       msg.template= "Watson found the words: "+image_text;
    }
    return msg;
}

var bestcolor = -1;
var colorscore = 0;
var c_id = 0;
var say = "";
var item;

for ( c_id=0; c_id < (msg.result.images[0].classifiers.length); c_id++ ){
    // find the best color, if any
    for( i =0; i<(msg.result.images[0].classifiers[c_id].classes.length); i++ ){
      var object = msg.result.images[0].classifiers[c_id].classes[i].class;
      if ( object.includes("color") ) {
        if( msg.result.images[0].classifiers[c_id].classes[i].score > colorscore){
            bestcolor = i;
            colorscore = msg.result.images[0].classifiers[c_id].classes[i].score;
        }
      }
    }

    var bestItem = 0;
    var itemScore = 0;
    for( i =0; i<(msg.result.images[0].classifiers[c_id].classes.length); i++ ){
      var object = msg.result.images[0].classifiers[c_id].classes[i].class;
      if ( !object.includes("color") ) {
        if( msg.result.images[0].classifiers[c_id].classes[i].score > itemScore){
//            bestItem = i;
            bestItem = 0;
            itemScore =  msg.result.images[0].classifiers[c_id].classes[i].score;
        }
      }
    }

     if( bestcolor != "-1") {
        // found a color
        item = msg.result.images[0].classifiers[c_id].classes[bestcolor].class + " " + msg.result.images[0].classifiers[c_id].classes[bestItem].class;
        bestcolor = -1;
    } else {
       item = msg.result.images[0].classifiers[c_id].classes[bestItem].class;
    }
//    say = say + " Watson's " + msg.result.images[0].classifiers[c_id].name + " classifier thinks this picture contains a " + item +".";
    say = say + " Watson thinks this picture contains a " + item +".";
}
msg.payload =  say;

var picInfo = msg.result.images[0].classifiers[0].classes;
var arrayLength = picInfo.length;

msg.template="<style>";
msg.template=msg.template+"table { width: 440px; margin-top: 10px; }";
msg.template=msg.template+"tr:nth-child(even){background-color: #f2f2f2;}";
msg.template=msg.template+"th, td { padding: 8px; text-align: left; border-bottom: 1px solid #ddd; width: 10%;}";
msg.template=msg.template+"</style>";

msg.template=msg.template+"<h2>"+say+"</h2><table span=100%><tr><th>Class</th><th>Confidence</th></tr>";
for (var i = 0; i < arrayLength; i++) {
  msg.template = msg.template + "<tr><td>" + picInfo[i].class + "</td><td>" + picInfo[i].score + "</td></tr>";
}
msg.template = msg.template + "</table>";

return msg;

msg.template = "<a href=\"https://twitter.com/"+msg.tweet.user.screen_name+"/status/"+msg.tweet.id_str+"\">Open Tweet</a>";
if( typeof msg.payload.retweeted_status != 'undefined') {
       msg.template = "<a href=\"https://twitter.com/"+msg.tweet.retweeted_status.user.screen_name+"/status/"+msg.tweet.retweeted_status.id_str+"\">Open Tweet</a>";
}

